import React, { useState } from "react";
import Book from "./book";
import bookspedia from "../bookspedia";
import Buttons from "./button";

import TurnedInNotIcon from '@mui/icons-material/TurnedInNot';
import BookmarkIcon from '@mui/icons-material/Bookmark';



function AllBooks() {

    const [item, setItem] = useState(bookspedia);

    const menuItems = [...new Set(bookspedia.map((book) => book.category))];

    const filterItem = (curcat) => {
        const newItem = bookspedia.filter((newVal) => {
            console.log(newVal.category === curcat)
            if (newVal.category === curcat) {
                return newVal
            }
        });

        setItem(newItem);
    };

    const [clicked, setClicked] = useState(false)
    function handleIconClick() {
        setClicked(!clicked)
    }
    //     const Button className = "bMark" onClick = {() => setClicked(!clicked)
    // }>
    //     { clicked?<BookmarkIcon /> : <TurnedInNotIcon />}
    // </Button >

    const bookMark = () => {
        return (clicked ? <BookmarkIcon /> : <TurnedInNotIcon />)
    };


    return <div className="featured">
        <h1 className="aBH1">All Books</h1>
        <Buttons
            filterItem={filterItem}
            setItem={setItem}
            menuItems={menuItems}
        />

        <Book item={item} bookMark={bookMark} />

    </div>
}

export default AllBooks;
