import React from "react";
import { useNavigate } from "react-router-dom";
import { useSelector } from 'react-redux';
import Book from "./book";

function Cart() {
    const { carts } = useSelector((item) => item.user)
    console.log(carts)

    const navigate = useNavigate();

    return (
        <div>
            <div className="" >
                <h1>Your Collection</h1>
            </div>
            <label htmlFor="" className="text-4xl" > Total Item : {carts.length} </label>
            {carts.map((Val) => {
                return (
                    <div className="container" data-aos="zoom-in-up" >
                        <div class="box-image">
                            <img src={Val.image} width={150} height={190} alt="..." />
                        </div>
                        <div class="box-text">
                            <h1 className="name">{Val.name}</h1>
                            <div className="catg">{Val.category}</div>
                            <button class="btn">Purchase</button>
                        </div>
                    </div>
                );
            })}
        </div>
    )
}

export default Cart;
