import React from "react";
import "../images/.."
import "../CSS/book.css";
import ShoppingCartIcon from '@mui/icons-material/ShoppingCart';
import { useNavigate } from "react-router-dom";
import axios from "axios";


const Book = ({ item }) => {

    const navigate = useNavigate();
    let isLogin = localStorage.getItem('islogin');

    // if (isLogin) {


    // }


    const handleclick = async (e) => {
        if (isLogin) {
            const { user } = sessionStorage.getItem("userdetails")
            e.preventDefault();
            try {
                const { data } = await axios.post('/addproducts', {
                    email: user.email,
                    item_id: item.id,
                    item_name: item.name
                });
                console.log(data)
                // if (data.success) {
                //     alert("Book added to cart");
                // }
            } catch (error) {
                alert(error.response.data.message);
                console.log(error.response.data.message);
            }
        } else {
            navigate("/login");
        }

    }



    return (
        <>
            {item.map((Val) => {
                return (
                    <div className="container" data-aos="zoom-in-up" ><div key={Val.id}></div>
                        <div className="box-image">
                            <img src={Val.image} width={150} height={190} alt="..." />
                            <button className="scart" onClick={handleclick} value={item} ><ShoppingCartIcon className="cart" /></button>
                        </div>
                        <div className="box-text">
                            <h1 className="name">{Val.name}</h1>
                            <div className="catg">{Val.category}</div>
                            <a href={Val.purchase}><button className="btn">Purchase</button></a>
                        </div>
                    </div>
                );
            })}
        </>
    )
}

export default Book;
