import React from "react";
import { useNavigate } from "react-router-dom";
import DeleteIcon from '@mui/icons-material/Delete';
import "../CSS/collection.css";
import axios from "axios";

const Cart = ({ item }) => {
    const { user } = sessionStorage.getItem("userdetails")
    console.log(user);

    const handleclick = async (e) => {
        try {
            const { data } = await axios.post('/deleteproduct', {
                email: user.email,
                item_id: item.id
            });

            if (data.success) {
                alert(data.message);
                toast.success("User login Successfully");
                navigate("/");
            }
        } catch (error) {
            console.log(error);
            alert(error);
        }
    }

    // useEffect(() => {
    const { cart } = async () => {
        const res = await axios.get('/all-items')
        console.log(res.data)
    }

    const [filteredUsers, setFilteredUsers] = useState(item);

    const handleFilter = (event) => {
        const value = event.target.value;
        // const filtered = item.filter(item => return ( ));
        const filtered = item.filter(item => {
            return item.id.find;
        });
        setFilteredUsers(filtered);
    };
    // cartitem()
    // }, [])

    const navigate = useNavigate();

    return (
        <div className="size">
            <div className="collection">
                <h1 className="title">Your Collection</h1>
                <label htmlFor="" className="text-4xl" > Total Item : {cart.itemCount} </label>
            </div>
            <div id="popular-container">
                <div className="featured">
                    {filteredUsers.map(user => (
                        <div className="container" data-aos="zoom-in-up" ><div key={user.id}></div>
                            <div class="box-image">
                                <img src={user.image} width={150} height={190} alt="..." />
                                <button className="scart" value={item} onClick={handleclick}><DeleteIcon className="cart" /></button>
                            </div>
                            <div className="box-text">
                                <h1 className="name">{user.name}</h1>
                                <div className="catg">{user.category}</div>
                                <a href={user.purchase}><button className="btn">Purchase</button></a>
                            </div>
                        </div>
                    ))} ;

                </div>
            </div>
        </div>
    )
}

export default Cart;
