import { useRef } from "react";
// import { FaBars, FaTimes } from "react-icons/fa";
import "../CSS/navbar.css";
import { NavLink, useNavigate } from "react-router-dom";
import { useSelector, useDispatch } from "react-redux";
import { authActions } from "../pages/redux/store";
import toast from "react-hot-toast";
import ShoppingCartIcon from '@mui/icons-material/ShoppingCart';

import MenuIcon from '@mui/icons-material/Menu';
// import { MenuItem } from "@mui/material";sss



const Navbar = () => {

    // global state
    let isLogin = useSelector((state) => state.isLogin);
    isLogin = isLogin || localStorage.getItem("userId");
    const dispatch = useDispatch();
    const navigate = useNavigate();
    //state
    // const [value, setValue] = useState();

    //logout
    const handleLogout = () => {
        try {
            dispatch(authActions.logout());
            toast.success("Logout Successfully");
            navigate("/login");
            localStorage.clear();
        } catch (error) {
            console.log(error);
        }
    };

    const navRef = useRef()
    const showNavbar = () => {
        navRef.current.classList.toggle(
            "responsive_nav"
        );
    };

    // <======== JS Program to Open & Close the Navigation Bar ========>
    const middleNavigation = document.querySelector("#navbar .middle-navigation");
    const middleNavigationLists = document.querySelectorAll("#navbar .middle-navigation ul li")
    const menuBar = document.querySelector("#navbar .right-navigation #menu-btn");

    menuBar.addEventListener("click", () => {
        middleNavigation.classList.toggle("show");
    })

    middleNavigationLists.forEach((navigation) => {
        navigation.addEventListener("click", () => {
            if (middleNavigation.classList.contains("show")) {
                middleNavigation.classList.toggle("show");
            }
        })
    })

    return (
        <header>
            <div id="navbar">
                <div className="left-navigation">
                    <i className="ri-store-2-line"></i>
                    BookRaze
                </div>
                <nav ref={navRef}>
                    {isLogin &&
                        <ul>
                            <div className="middle-navigation">
                                <li><NavLink exact activeClassName="active" className="link" to="/">Home</NavLink></li>
                                <li><NavLink activeClassName="active" className="link" to="/about">About</NavLink></li>
                                <li><NavLink activeClassName="active" className="link" to="/allbooks">All Books</NavLink></li>
                            </div>
                            <div className="right-navigation">
                                <li><NavLink id="cart-btn" activeClassName="active" className="link" to="/collection"><ShoppingCartIcon /></NavLink></li>
                                <li><NavLink activeClassName="active" onClick={handleLogout} className="link" to="/">Logout  </NavLink></li>
                            </div>
                        </ul>
                    }

                    {!isLogin && <ul>
                        <div className="middle-navigation">
                            <li><NavLink exact activeClassName="active" className="link" to="/">Home</NavLink></li>
                            <li><NavLink activeClassName="active" className="link" to="/about">About</NavLink></li>
                            <li><NavLink activeClassName="active" className="link" to="/allbooks">All Books</NavLink></li>
                        </div>
                        <div className="right-navigation">
                            <li><NavLink id="login-btn" activeClassName="active" className="link" to="/login">Login</NavLink></li>
                            <li><NavLink activeClassName="active" className="link" to="/register">Register</NavLink></li>
                        </div>
                    </ul>}

                    <div class="right-navigation">
                        <div id="menu-btn" class="ri-menu-line hide"><MenuIcon /></div>
                    </div>

                    {/* <button
                        className="nav-btn nav-close-btn"
                        onClick={showNavbar}>
                        <FaTimes />
                        <Outlet />
                    </button> */}
                </nav>

                {/* <button
                    className="nav-btn"
                    onClick={showNavbar}>
                    <FaBars />
                </button> */}

            </div>
        </header>
    );
}

export default Navbar;
